import React, { Component } from "react";
import axios from "axios";
import { faEnvelope } from "@fortawesome/free-regular-svg-icons";
import { faLock } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import "../shared/App.css";
import './Main.css'

class Register_2 extends Component {
  constructor(props) {
    super(props);
    this.state = {
      nickname: "",
      age:"",
    };
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }
  handleChange(event) {
    const target = event.target;
    this.setState({
      [target.name]: target.value,
    });
  }
  handleSubmit(event) {
    event.preventDefault();
    axios
      .put("http://localhost:8000/profile", {
        Nickname: this.state.nickname,
        Age: this.state.age,
      })
      .then((response) => {
        console.log(response.data);
        this.props.history.push("/login");
      });
  }
  render() {
    return (
      <section className="hero is-warning is-large">
        <div className="hero-body register_1">
          <div className="container">
            <div className="columns is-centered">
              <div className="border1 column is-6-tablet is-5-desktop is-4-widescreen">
                <form onSubmit={this.handleSubmit} className="box">
                  <div className="field">
                    <label className="label">닉네임</label>
                    <div className="control border has-icons-left">
                      <input
                        type="text"
                        placeholder="닉네임을 입력하세요."
                        className="input"
                        name="nickname"
                        value={this.state.nickname}
                        onChange={this.handleChange}
                        required
                      />
                      <span className="icon is-small is-left">
                        <FontAwesomeIcon icon={faEnvelope} />
                      </span>
                    </div>
                  </div>
                  <div className="field">
                    <label className="label">나이</label>
                    <div className="control border has-icons-left">
                      <input
                        type="number"
                        placeholder="나이를 입력하세요."
                        className="input"
                        name="age"
                        value={this.state.age}
                        onChange={this.handleChange}
                        required
                      />
                      <span className="icon is-small is-left">
                      <img alt="" src="../number1.png" />
                      </span>
                    </div>
                  </div>
                  <br/>
                  <br/>
                  <br/>
                  <br/>
                  <div className="field">
                    <button
                      className="button is-fullwidth div2"
                      type="submit"
                    >
                      회원가입
                    </button>
                  </div>
                </form>
              </div>
            </div>
          </div>
        </div>
      </section>
    );
  }
}

export default Register_2;
